mapper.select:

SELECT user_name, mem_num, id, user_pwd, nickname, contact, email, birth_date, gender, address
FROM
member_tbl
WHERE id = #{id}
AND user_pwd = #{user_pwd}

mapper.update:

update member_tbl
set user_name=#{user_name}, user_pwd=#{user_pwd}, nickname=#{nickname}, contact=#{contact}, email=#{email}, 
birth_date=#{birth_date}, address=#{address}
where id=#{id}
